using GameFramework.Network;

namespace Genesis.GameClient
{
    public class LCGiveEnergyHandler : BasePacketHandler
    {
        public override int OpCode { get { return NetworkHelper.GetOpCode(PacketType.LobbyServerToClient, 3106); } }

        public override void Handle(object sender, Packet packet)
        {
            base.Handle(sender, packet);
            Handle(sender, packet as LCGiveEnergy);
        }

        public static void Handle(object sender, LCGiveEnergy response)
        {
            GameEntry.Data.Friends.ModifyFriend(response.FriendInfo);
            GameEntry.Event.Fire(sender, new FriendDataChangedEventArgs(FriendDataChangedEventArgs.ChangeMode.ListRefreshed));

            if (response.HasTodayGiveCount) // 有这个字段说明是发送者
            {
                GameEntry.Data.Friends.ModifyTodayGiveCount(response.TodayGiveCount);
                GameEntry.Event.Fire(sender, new EnergyGivenToFriendEventArgs(response.FriendInfo.PlayerInfo.Id,
                    System.Math.Max(GameEntry.ServerConfig.GetInt(Constant.ServerConfig.Friend.DailyEnergyGiveTimes, 10) - response.TodayGiveCount, 0)));
            }
        }
    }
}
